import { useState } from "react";
import { useDispatch } from "react-redux";
import { registerUser } from "../actions/userAction.js";
import { useHistory } from "react-router-dom";

export default function Register() {
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [confirm, setConfirm] = useState("");
  
  const dispatch = useDispatch();
  const history = useHistory(); // add this line to get the history object

  function register() {
    if (password != confirm) {
      alert("Passwords do not match!");
    } else {
      const user = {
        firstName,
        lastName,
        email,
        password,
      };
      console.log(user);
      dispatch(registerUser(user));
      history.push("/login"); // add this line to redirect to the login page
    }
  }

  return (
    <div>
      <div className="row justify-content-center mt-5">
        <div className="col-md-5 mt-5 p-2">
          <h1>Register</h1>
          <div>
            <input
              required
              type="text"
              placeholder="first name"
              className="form-control"
              value={firstName}
              onChange={(e) => {
                setFirstName(e.target.value);
              }}
            />
            <input
              required
              type="text"
              placeholder="last name"
              className="form-control"
              value={lastName}
              onChange={(e) => {
                setLastName(e.target.value);
              }}
            />
            <input
              required
              type="text"
              placeholder="email"
              className="form-control"
              value={email}
              onChange={(e) => {
                setEmail(e.target.value);
              }}
            />
            <input
              required
              type="password"
              placeholder="password"
              className="form-control"
              value={password}
              onChange={(e) => {
                setPassword(e.target.value);
              }}
            />
            <input
              required
              type="password"
              placeholder="confirm password"
              className="form-control"
              value={confirm}
              onChange={(e) => {
                setConfirm(e.target.value);
              }}
            />
            <button onClick={register} className="btn mt-4">
              {" "}
              Register{" "}
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}
