export const addToCart=(pizza, quantity, size)=>dispatch=>{

    var cartItems = {
        name : pizza.name,
        _id : pizza._id,
        image : pizza.image,
        size : size,
        quantity : quantity,
        prices : pizza.prices,
        totalPrice: pizza.prices[0][size] * quantity
    }

    dispatch({type:'ADD_TO_CART', payload: cartItems});

}
