import { useSelector, useDispatch } from "react-redux";
import { addToCart, deleteFromCart } from "../actions/cartAction";

export default function Cart() {
  const cartState = useSelector((state) => state.cartReducer);
  const cartItems = cartState.cartItems;
  var subTotal = cartItems.reduce((x, item) => x + item.totalPrice, 0);
  const dispatch = useDispatch();
  return (
    <div className="container">
      <div className="row justify-content-center">
        <div className="col-md-8">
          <h1 className="p-4 mt-4 bg-warning text-center">Shopping Cart</h1>
          {cartItems.map((item) => {
            return (
              <div
                key={item.id}
                className="flex-container my-3"
                style={{ boxShadow: "2px 2px 4px rgba(0, 0, 0, 0.3)" }}
              >
                <div className="m-1 w-75 p-2">
                  <p>
                    {item.size} {item.name}
                    {" x"}
                    {item.quantity}
                  </p>
                  <img
                    src={item.image}
                    alt="Example"
                    style={{ height: "200px", width: "200px" }}
                  />
                  <p style={{ marginTop: "2rem" }}>
                    Price: {item.totalPrice} TK
                  </p>
                  <div className="d-flex align-items-center mt-2">
                    <i
                      className="fa fa-plus mr-2"
                      aria-hidden="true"
                      onClick={() => {
                        dispatch(addToCart(item, item.quantity + 1, item.size));
                      }}
                    ></i>{" "}
                    <span className="mx-2">{item.quantity}</span>{" "}
                    <i
                      className="fa fa-minus mr-2"
                      aria-hidden="true"
                      onClick={() => {
                        dispatch(addToCart(item, item.quantity - 1, item.size));
                      }}
                    ></i>
                  </div>
                </div>
                <i
                  className="fa fa-trash align-self-center mx-2"
                  aria-hidden="true"
                  onClick={() => dispatch(deleteFromCart(item))}
                ></i>
              </div>
            );
          })}
          <h2
            className="p-4 bg-warning"
            style={{
              marginTop: "18px",
              boxShadow: "2px 2px 4px rgba(0, 0, 0, 0.3)",
            }}
          >
            Total: {subTotal} Tk{" "}
            <div className="m-2 mt-3 d-flex justify-content-center">
              <button className="btn btn-lg btn-success">Confirm Order</button>
            </div>
          </h2>
        </div>
      </div>
    </div>
  );
}
