export const cartReducer = (state = { cartItems: [] }, action) => {
  switch (action.type) {
    case "ADD_TO_CART":
      const exists = state.cartItems.find(
        (item) => item._id === action.payload._id
      );
      if(exists) { 
        return { 
          ...state,
          cartItems : state.cartItems.map(item => item._id === action.payload._id ? action.payload._id : item)
        }

      } else {


      }
      return {
        ...state,
        cartItems: [...state.cartItems, action.payload],
      };

    default:
      return state;
  }
};
