import { combineReducers } from 'redux';
import { getAllPizzasReducer } from './reducers/pizzaReducer';
import thunk from 'redux-thunk';
import { legacy_createStore as createStore, applyMiddleware } from 'redux-devtools-extension';

const finalReducer = combineReducers({
  getAllPizzasReducer: getAllPizzasReducer,
});

const composeEnhancers = composeWithDevTools({})
const initialState = {};
const store = createStore(finalReducer, initialState, composeEnhancers(applyMiddleware));

export default store;
