import React, { useState } from "react";
import { useDispatch } from "react-redux";
import { userLogin } from "../actions/userAction.js";
import { Link } from "react-router-dom";
import "../login.css";

export default function Login() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [errorMessage, setErrorMessage] = useState(""); // new state variable
  
  const dispatch = useDispatch();

  function login() {
    const user = {
      email,
      password,
    };
    dispatch(userLogin(user))
      .catch((error) => {
        setErrorMessage(error.response.data.message); // update error message state variable
      });
  }

  return (
    <div className="login-container">
      <div className="login-card">
        <h1 className="login-heading">Login</h1>
        {errorMessage && <div className="alert alert-danger">{errorMessage}</div>} {/* display error message if it exists */}
        <div className="form-group">
          <input
            type="text"
            placeholder="Email"
            className="form-control"
            required
            value={email}
            onChange={(e) => {
              setEmail(e.target.value);
            }}
          />
        </div>
        <div className="form-group">
          <input
            type="password"
            placeholder="Password"
            className="form-control"
            required
            value={password}
            onChange={(e) => {
              setPassword(e.target.value);
            }}
          />
        </div>
        <div className="form-group">
          <button onClick={login} className="btn btn-primary p-2">
            Login
          </button>
        </div>
        <p className="register-link">
          First Byte? <Link to="/register">Create Account</Link>
        </p>
      </div>
    </div>
  );
}
