export const getPizzaReducer = (state = { pizza: [] }, action) => {
  switch (action.type) {
    case "GET_PIZZA_REQUEST":
      return {
        loading: true,
        //...state,
        pizza: []
      };
    case "GET_PIZZA_SUCCESS":
      return {
        loading: false,
        pizza: Array.isArray(action.payload) ? action.payload : []
        //pizza: action.payload,
      };
    case "GET_PIZZA_FAILED":
      return { 
        error: action.payload, 
        loading: false,
        pizza: [] 
      };
    default:
      return state;
  }
};
