import React, { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { updatePizza } from "../../actions/pizzaAction";
import Loader from "./components/Loader";
import Message from "./components/Message";

export default function UpdatePizza({ match, history }) {
  const dispatch = useDispatch();
  const pizzaId = match.params.id;

  const [name, setName] = useState("");
  const [description, setDescription] = useState("");
  const [category, setCategory] = useState("");
  const [image, setImage] = useState("");
  const [sizes, setSizes] = useState([]);
  const [prices, setPrices] = useState([]);
  const [message, setMessage] = useState(null);

  const pizzaDetails = useSelector((state) => state.pizzaDetails);
  const { loading, error, pizza } = pizzaDetails;

  useEffect(() => {
    if (pizza && pizza._id === pizzaId) {
      setName(pizza.name);
      setDescription(pizza.description);
      setCategory(pizza.category);
      setImage(pizza.image);
      setSizes(pizza.size);
      setPrices(pizza.prices[0]);
    }
  }, [pizza, pizzaId]);

  const submitHandler = (e) => {
    e.preventDefault();

    const pricesArray = [];
    sizes.forEach((size, index) => {
      const price = prices[index];
      const priceObject = {
        [size]: parseInt(price, 10),
      };
      pricesArray.push(priceObject);
    });

    const pizza = {
      _id: pizzaId,
      name,
      description,
      category,
      image,
      size: sizes,
      prices: pricesArray,
    };

    dispatch(updatePizza(pizza));
    setMessage("Pizza updated successfully!");
  };

  return (
    <div className="d-flex justify-content-center align-items-center mt-4 p-4">
      <div className="text-center shadow p-4">
        <h1>Update Pizza</h1>
        {loading && <Loader />}
        {error && <Message variant="danger" message={error} />}
        {loading ? (
          <Loader />
        ) : error ? (
          <Message variant="danger" message={error} />
        ) : (
          <div className="form-group">
            <form onSubmit={submitHandler}>
              <table>
                <tbody>
                  <tr>
                    <td>
                      <label htmlFor="name">Name:</label>
                    </td>
                    <td>
                      <input
                        type="text"
                        className="form-control"
                        id="name"
                        value={name}
                        onChange={(e) => setName(e.target.value)}
                        required
                      />
                    </td>
                  </tr>
                  <tr>
                    <td>
                      <label htmlFor="description">Description:</label>
                    </td>
                    <td>
                      <textarea
                        className="form-control"
                        id="description"
                        value={description}
                        onChange={(e) => setDescription(e.target.value)}
                        required
                      ></textarea>
                    </td>
                  </tr>
                  <tr>
                    <td>
                      <label htmlFor="category">Category:</label>
                    </td>
                    <td>
                      <input
                        type="text"
                        className="form-control"
                        id="category"
                        value={category}
                        onChange={(e) => setCategory(e.target.value)}
                        required
                      />
                    </td>
                  </tr>
                  <tr>
                    <td>
                      <label htmlFor="image">Image URL:</label>
                    </td>
                    <td>
                      <input
                        type="text"
                        className="form-control"
                        id="image"
                        value={image}
                        onChange={(e) => setImage(e.target.value)}
                        required
                      />
                    </td>
                  </tr>
                  <tr>
                    <td>
                      <label htmlFor="prices">Prices:</label>
                    </td>
                    <td>
                      <input
                        type="text"
                        className="form-control"
                        id="prices"
                        value={prices}
                        onChange={(e) => setPrices(e.target.value)}
                        required
                      />
                    </td>
                  </tr>
                  <tr>
                    <td></td>
                    <td>
                      <button
                        type="submit"
                        className="btn btn-primary mt-3 p-2"
                      >
                        Update Pizza
                      </button>
                    </td>
                  </tr>
                </tbody>
              </table>
            </form>
            {message && <h3 className="mt-3">{message}</h3>}
          </div>
        )}
      </div>
    </div>
  );
}
