import React, { useState } from "react";
import { useDispatch } from "react-redux";
import { getPizza } from "../../actions/pizzaAction";

export default function Admin() {
  const dispatch = useDispatch();

  const [name, setName] = useState("");
  const [description, setDescription] = useState("");
  const [category, setCategory] = useState("");
  const [image, setImage] = useState("");
  const [sizes, setSizes] = useState([]);
  const [prices, setPrices] = useState([]);
  const [message, setMessage] = useState(null);

  const submitHandler = (e) => {
    e.preventDefault();

    const pizza = {
      name,
      description,
      category,
      image,
      size: sizes,
      prices,
    };

    dispatch(getPizza(pizza));
    setMessage("New pizza added successfully!");
  };

  return (
    <div>
      <h1>Add New Pizza</h1>
      <div className="form-group">
        <form onSubmit={submitHandler}>
          <label htmlFor="name">Name:</label>
          <input
            type="text"
            className="form-control"
            id="name"
            placeholder="Enter pizza name"
            onChange={(e) => setName(e.target.value)}
            required
          />
          <label htmlFor="description">Description:</label>
          <textarea
            className="form-control"
            id="description"
            placeholder="Enter pizza description"
            onChange={(e) => setDescription(e.target.value)}
            required
          ></textarea>
          <label htmlFor="category">Category:</label>
          <select
            className="form-control"
            id="category"
            onChange={(e) => setCategory(e.target.value)}
            required
          >
            <option value="nonveg">Non-Veg</option>
            <option value="veg">Veg</option>
          </select>
          <label htmlFor="image">Image URL:</label>
          <input
            type="text"
            className="form-control"
            id="image"
            placeholder="Enter pizza image URL"
            onChange={(e) => setImage(e.target.value)}
            required
          />
          <label htmlFor="sizes">Sizes:</label>
          <input
            type="text"
            className="form-control"
            id="sizes"
            placeholder="Enter pizza sizes (comma separated)"
            onChange={(e) => setSizes(e.target.value.split(","))}
            required
          />
          <label htmlFor="prices">Prices:</label>
          <input
            type="text"
            className="form-control"
            id="prices"
            placeholder="Enter pizza prices (comma separated)"
            onChange={(e) => setPrices(e.target.value.split(","))}
            required
          />
          <button type="submit" className="btn btn-primary mt-3">
            Add Pizza
          </button>
        </form>
        {message && <h3 className="mt-3">{message}</h3>}
      </div>
    </div>
  );
}
