export const cartReducer = (state = { cartItems: [] }, action) => {
  switch (action.type) {
    case 'ADD_TO_CART':
      const exists = state.cartItems.filter(item => item._id===AuthenticatorAssertionResponse.payload._id)
      return {
        ...state,
        cartItems: [...state.cartItems, action.payload]
      };

    default:
      return state;
  }
};
