export const getAllPizzasReducer = (state = {}, action) => {
  switch (action.type) {
    case "GET_PIZZA_REQUEST":
      return { ...state };
    case "GET_PIZZA SUCCESS":
      return { pizza: action.payload };
    case "GET_PIZZA_FAILED":
      return { error: action.payload };
    default:
      return state;
  }
};
